mapper.update:
update bbs_article

	
		art_label = #{art_label},
	
	
		title = #{title},
	
	
		content = #{content}
	


	id = #{id}

mapper.select:
select count(1) from bbs_article a 
left join bbs_star s on a.id = s.aid 
left join bbs_user u on u.id = s.uid where u.id = #{uid}

select a.id,a.content,a.uid,a.date,a.author,a.art_label,a.sid,a.reply_count,a.title 
from bbs_article a 
left join bbs_star s on a.id = s.aid 
left join bbs_user u on u.id = s.uid where u.id = #{uid}
limit #{pageQueryVo.startIndex},#{pageQueryVo.pageSize}
